mapper.insert:
insert into seckill (seckill_id, name, number, 
  start_time, end_time, create_time
  )
values (#{seckillId,jdbcType=BIGINT}, #{name,jdbcType=VARCHAR}, #{number,jdbcType=INTEGER}, 
  #{startTime,jdbcType=TIMESTAMP}, #{endTime,jdbcType=TIMESTAMP}, #{createTime,jdbcType=TIMESTAMP}
  )

mapper.update:
update seckill
set number = number - 1,
where seckill_id = #{seckillId} and start_time  <=  #{killTime}
AND end_time  >=  #{killTime} AND number > 0;
